INSERT INTO module1 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix) VALUES ('Console Commands', 'exercise one', 5, 'From the Terminal/Console and using only the command line, create the following', 'A new folder with the name of "first_day_stuff"', 'A new HTML file with the name of "first-day.html"', 'Open the current folder containing the new HTML file', 'Create multiple directories/folders with the names "one_folder" and "second_folder" in one command.', 'Create multiple files witht he names "one.html" and "two.html" in one command in the "first_day_stuff" directory.');
INSERT INTO module1 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix) VALUES ('Intro to Console Bash', 'exercise two', 5, 'From the Terminal/Console, using only the command line', 'Create a new folder with the name of "self-destructing-folder"', 'Create a new txt file with the name "secret-message.txt"', 'Copy "secret-message.txt" to the inside of "self-destructing-folder".', 'Delete the original "secret-message.txt" file.', 'Delete the "self-destructing-folder".');
INSERT INTO module1 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix, stepSeven, stepEight, stepNine, stepTen, stepEleven, stepTwelve) VALUES ('My First HTML', 'exercise three', 5, 'In a new HTML file, create the basic structure of an HTML document and include the following in it/n HINT You should be looking up at the screen pretty often', 'DOCTYPE declaration', 'Head tag with a title tag', 'H1 tag with a title of your choice', 'Embed an image', 'Create the following three links on your page', 'One link that is `target="_blank"` so that it opens a new tab when clicked on.','Make the second link bold.','Make the third link a placeholder so it goes nowhere.', 'Bonus Create an ordered list of steps to make a sandwich.\n Create an unordered list of 5 bands/musicians you like.', 'Bonus Create a table with 2 columns (animal class and animal name) and 4 rows of animals.\n Use an alternate way of separating links without line breaks.', 'Bonus Embed a Youtube video of your favorite band/musician.');
INSERT INTO module1 (title, description, countDown, stepOne) VALUES ('HTML Git', 'exercise four', 5, 'Create a basic HTML page with your own information that looks similar to the design shown on screen.');
