INSERT INTO module3 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour) VALUES ('Prompt Sushi', 'exercise seven', 5, 'Write JavaScript code that does the following', 'Using a confirm, ask the user, "Do you like ?" Store their response in a variable.', 'Using a prompt, ask the user, "What kind of  do you like?" Store their response in a variable.', 'Alert both variables to the screen.');
