INSERT INTO module3 (title, description, countDown, stepOne, stepTwo, stepThree) VALUES ('Arrays Activity', 'exercise twelve', 5, 'With a partner, take a few moments to look over the code in the file above.', 'Above each `console.log()` write a comment "predicting" what you think the output will be.', 'Hint Comments are the grayed lines that begin with `//`. These lines are ignored by JavaScript, and they allow you to explain your code. Commenting your code is an extremely useful habit to get into as it allows other developers to more easily read your code. It will also help you better understand your own applications when you look back at them.');
INSERT INTO module3 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix, stepSeven) VALUES ('Bands', 'exercise thirteen', 5, 'Create a website that accomplishes the following', 'Create an array of your favorite bands.', "With a prompt, ask the user's favorite band.", "If it's one of your favorites, alert 'YEAH I LOVE THEM!'", "If it's not, alert 'Nah. They're pretty lame.'", 'Hint You will need to research how to use `.indexOf()`.', 'Hint You will need to research how to use `.toLowerCase()`.');
INSERT INTO module3 (title, description, countDown, stepOne, stepTwo) VALUES ('JS Dissect', 'exercise fourteen', 5, 'Take a few minutes to quickly look through the above file. With a partner, discuss what you expect to happen when the code is run.', 'Prepare to share your thoughts with the class.');
