INSERT INTO module4 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive) VALUES ('Sandwich Click', 'exercise six', 5, 'Add in the missing code such that clicking any of the sandwiches causes…', 'An alert message to popup saying something snarky about the sandwich type.', 'A second alert message that displays to the user the number of that specific sandwich they’ve eaten.', 'Hint You will need counter variables.', 'Bonus Add an image to the `image-div` on the click event.');
INSERT INTO module4 (title, description, countDown, stepOne) VALUES ('Trigger Random', 'exercise seven', 5, 'Add in the missing code such that clicking the big blue button triggers a random number (between 1 and 1000) to be selected and prominently displayed in the random-number div.');
INSERT INTO module4 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour) VALUES ('Lottery Generator', 'exercise eight', 5, 'Using the code from the previous random number generator as a starting point, create a lottery generator.', 'In our case, the lottery number should pick a random 9 digit number (and always 9 digits). As an example 886563264.', 'Display this number in the random-number div.', 'Then when a user clicks again, have the code create a new row with the latest number at the top.');
INSERT INTO module4 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive) VALUES ('Number Checker', 'exercise nine', 5, 'Using the number-checker.html as a starting point, add code so that', 'A computer picks a random number between 1 and 4.', 'Users then “click” the buttons numbered 1 – 4.', "If the user’s number matches the computer’s number, display text informing them that they've won in the 'Result' panel. Otherwise, display text informing them that they've lost.", 'If you finish early, try to improve the aesthetics.');
INSERT INTO module4 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix) VALUES ('Captain Planet Game', 'exercise ten', 5, 'Look at the jQuery API Docs https//api.jquery.com/ and add a button of your own that gives Captain Planet a new power.', 'Click to… Stretch Captain Planet!', 'Click to… Trigger a maniacal laugh!', 'Click to… Create clones of Captain Planet!', 'Click to… Create fire or water (hint images)!', 'Slack out a screenshot of the working example.');
INSERT INTO module4 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive) VALUES ('Fridge Game', 'exercise eleven', 5, 'Working in groups of 3, complete the code for the fridge activity such that', 'JavaScript dynamically generates buttons for each of the letters on the screen.', 'Clicking any of the buttons leads the SAME letter to be displayed on the screen.', 'Hitting the clear button erases all of the letters from the fridge.', 'Note This is a challenging exercise. You may want one person to type, while the other two watch over to catch bugs and/or research necessary snippets.');
