INSERT INTO module5 (title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix, stepSeven, stepEight) VALUES ('Floats Box Model', 'exercise seventeen', 5, 'DO NOT TOUCH THE HTML.', 'Get the `#right` div next to the `#left` div', "Bonus Don't change any widths.", 'Get the Background color to extend past the two floated divs.', 'Make the `#right` div end where the #left div ends. Make it so you can scroll to see the text.', 'Make the `#after` div appear after the text boxes.', 'Center the `#text-wrapper`.', 'Make the space around the text with the navy background so it looks like the picture below.');
