INSERT INTO module12 (id, Title, description, countDown, stepOne, stepTwo, stepThree, stepFour, stepFive, stepSix, stepSeven) VALUES ('2', 'favoriteDB', 'exercise 2', 15, 'For the table favorite_foods', 'Create the column food which can take in a 50 character string and cannot be NULL,\n  Create the column score which can take in an integer', ' For the table favorite_songs.', 'Create the column song which can take in a 100 character string and cannot be NULL,\n   Create the column artist which can take in a 50 character string,\n   Create the column score which can take in an integer', ' For the table favorite_movies.', ' Create a numeric column called id which automatically increments and cannot be null,\n   Create the column film which can take in a string and cannot be NULL,\n   Create the column five_times which can take in a boolean,\n   create the column score which can take in an integer,\n   Set the primary key of the table to id', 'BONUS Go online and look into how one might go about adding data into a table.');
